data.value:
wd <- getwd()
data_directory <- "{0}"
setwd(data_directory)

chain_nos <- c({1})
parameter_names <- c({2})
output_names <- c({3})

# load data

chain_datas <- list()
posteriors <- list()

for (chain_no in chain_nos)
{{
  chain_data <-
    read.csv(paste(".", chain_no, "chaindata.csv", sep = "/"),
             header = TRUE)
  chain_datas[[chain_no]] <- chain_data

  posterior <- list()

  for (output_name in output_names)
  {{
    outputs <-
      read.csv(paste(".", chain_no, paste0(output_name, ".csv"), sep = "/"),
               header = TRUE)
    posterior[[output_name]] <- outputs
  }}

  posteriors[[chain_no]] <- posterior
}}

setwd(wd)

convergence_begin <- {4}

nrows <- lapply(chain_datas, function(df)
  nrow(df))
convergence_end <- min(as.numeric(nrows))

if (convergence_begin >= convergence_end)
  stop("Invalid convergence begin!")

# plot proposal histograms

for (parameter_name in parameter_names)
{{
  all_values <- sapply(
    chain_nos,
    function(chain_no)
    {{
     parameter_data <- chain_datas[[chain_no]][[parameter_name]]
     return(parameter_data[convergence_begin:convergence_end])
    }},
    simplify = T
    )

  all_values <- unlist(all_values)

  hist(all_values,
       main = paste(parameter_name, "Values"),
       xlab = parameter_name)
}}

# trace outputs across chains

for (output_name in output_names)
{{
  for (chain_no in chain_nos)
  {{
    output_data <- posteriors[[chain_no]][[output_name]]

    x <- output_data[, 1]
    y <- output_data[, convergence_begin]
    xlab <- colnames(output_data)[0]
    ylab <- output_name
    main <- paste0("Trace of ", output_name, " for chain #", chain_no)

    plot(
      x,
      y,
      xlab = xlab,
      ylab = ylab,
      main = main,
      type = "l",
      col = "black",
      pch = ".",
      lty = 1
    )

    for (i in (convergence_begin + 1):convergence_end)
    {{
      y <- output_data[, i]
      lines(x, y, col = "black", lty = 1)
    }}
  }}
}}

# MCMC diagnostics

if (!require(coda))
  stop("Install CODA package!")

coda_lists <- lapply(chain_datas, function(chain_data)
{{
  parameter_columns <-
    lapply(parameter_names, function(parameter_name)
    {{
      chain_data[1:convergence_end, parameter_name]
    }})
  parameter_matrix <- do.call(cbind, parameter_columns)
  colnames(parameter_matrix) <- parameter_names
  return (mcmc(parameter_matrix))
}})

coda_list <- as.mcmc.list(coda_lists)

plot(coda_list)
gelman.diag(coda_list)
gelman.plot(coda_list)
