rvis_sensitivity_sampleSize <- {0}
rvis_sensitivity_factors <- c({1})
rvis_sensitivity_omegaFromCukier <- {2}
rvis_sensitivity_qfs <- c({3})
rvis_sensitivity_qfargs <- list({4})

rvis_sensitivity_nFactors <- length(rvis_sensitivity_factors)
rvis_sensitivity_omega <- NULL

if (rvis_sensitivity_omegaFromCukier)
{{
  rvis_sensitivity_omega <- fast::freq_cukier(rvis_sensitivity_nFactors)
  rvis_sensitivity_maxOmega <- max(rvis_sensitivity_omega)
  rvis_sensitivity_sampleSize <- rvis_sensitivity_maxOmega * rvis_sensitivity_nFactors + 1
}}

rvis_sensitivity_design <- sensitivity::fast99(
  model = NULL, 
  factors = rvis_sensitivity_factors, 
  n = rvis_sensitivity_sampleSize, 
  M = 4, 
  omega = rvis_sensitivity_omega, 
  q = rvis_sensitivity_qfs, 
  q.arg = rvis_sensitivity_qfargs
)

wd <- getwd()
data_directory <- "{0}"
